mapper.select:
SELECT A.RNUM , A.TAX_NO , A.TAX_DATE , S.STORE_NO , S.STORE_NAME , S.STORE_ADDRESS , S.STORE_REP , RS.REC_TOTALMONEY , A.REC_CODE FROM (SELECT ROWNUM RNUM , B.TAX_NO , B.TAX_DATE , B.REC_CODE FROM (SELECT C.TAX_NO , TO_CHAR(C.TAX_DATE,'yyyy-MM-dd') AS TAX_DATE , C.REC_CODE FROM TAXBILL C ORDER BY C.TAX_NO ASC ) B
WHERE ROWNUM <= #{ endRow }
) A JOIN RECEIVE_STORE RS ON (RS.REC_CODE = A.REC_CODE) JOIN STORE_ORDER SO ON (RS.ORDER_NO = SO.ORDER_NO) JOIN STORE S ON(S.STORE_NAME = RS.STORE_NAME) WHERE A.RNUM >= #{ startRow } ORDER BY A.TAX_NO ASC
