mapper.select:
SELECT
    COUNT(*)
 FROM INTEREST A
WHERE A.ITEM_NO = ${ itemNo }
  AND A.STORE_NAME = #{ storeName }

mapper.delete:
DELETE FROM INTEREST
 WHERE STORE_NAME = #{ storeName }
   AND ITEM_NO = ${ itemNo }

mapper.select:
SELECT A.RNUM , A.CART_AMOUNT , A.CATEGORY_NO , A.ITEM_NO , A.CART_NO , A.ITEM_NAME , A.ITEM_STANDARD , A.ITEM_SALES , A.ITEM_SOLDOUT_YN , A.ITEM_MAKE , A.FILE_NO , A.ORIGIN_NAME , A.FILE_NAME , A.BALANCE , A.TOTAL_PRICE FROM (SELECT ROWNUM RNUM , B.CART_AMOUNT , B.CATEGORY_NO , B.ITEM_NO , B.CART_NO , B.ITEM_NAME , B.ITEM_STANDARD , B.ITEM_SALES , B.ITEM_SOLDOUT_YN , B.ITEM_MAKE , B.FILE_NO , B.ORIGIN_NAME , B.FILE_NAME , B.BALANCE , B.TOTAL_PRICE FROM (SELECT C.CART_AMOUNT , C.CATEGORY_NO , C.ITEM_NO , C.CART_NO , E.ITEM_NAME , E.ITEM_STANDARD , E.ITEM_SALES , E.ITEM_SOLDOUT_YN , E.ITEM_MAKE , F.FILE_NO , F.ORIGIN_NAME , F.FILE_NAME , G.BALANCE , H.TOTAL_PRICE FROM CART C LEFT JOIN ITEM_INFO E ON (C.ITEM_NO = E.ITEM_NO) LEFT JOIN ITEM_FILE F ON (C.ITEM_NO = F.ITEM_NO) LEFT JOIN BALANCE G ON (C.STORE_NAME = G.STORE_NAME) LEFT JOIN ORDER_HANDLER H ON (C.ITEM_NO = H.TOTAL_PRICE)
LEFT JOIN ITEM_CATEGORY D ON (C.CATEGORY_NO = D.CATEGORY_NO)
D.CATEGORY_NAME LIKE '%' || #{ searchValue } || '%'
E.ITEM_NAME LIKE '%' || #{ searchValue } || '%'
AND C.STORE_NAME = #{ storeName } AND C.CART_YN = 'Y'
ORDER BY C.ITEM_NO ASC ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

mapper.delete:
DELETE FROM CART
WHERE STORE_NAME = #{ storeName }
  AND CART_YN = 'Y'
  AND ITEM_NO = #{ itemNo }

mapper.insert:
INSERT INTO CART
(
      CART_AMOUNT
    , CATEGORY_NO
    , ITEM_NO
    , CART_NO
    , STORE_NAME
    , CART_YN
)
VALUES
(
      #{ cartAmount }
    , #{ categoryNo }
    , #{ itemNo }
    , SEQ_CART_NO_TEST.NEXTVAL
    , #{ storeName }
    , 'N'
)

    SELECT SEQ_CART_NO_TEST.CURRVAL FROM dual

INSERT INTO ORDER_HANDLER
(
    ORDER_NO
  , CART_NO
  , TOTAL_PRICE
)
VALUES
(
    #{orderNo}
  , #{cartNo}
  , #{totalPrice}
)

mapper.delete:
DELETE FROM CART
WHERE STORE_NAME = #{ storeName }
  AND ITEM_NO = #{ itemNo }

mapper.select:
SELECT COUNT(A.CART_NO) FROM ORDER_HANDLER A JOIN STORE_ORDER B ON (B.ORDER_NO = A.ORDER_NO) JOIN CART C ON (A.CART_NO = C.CART_NO)
TO_CHAR(B.ORDER_DATE, 'YY/MM/DD') = #{ orderDate }
AND C.STORE_NAME = #{ storeName }

SELECT A.RNUM , A.CART_AMOUNT , A.CATEGORY_NO , A.ITEM_NO , A.CART_NO , A.ITEM_NAME , A.ITEM_STANDARD , A.ITEM_SALES , A.ITEM_MAKE , A.FILE_NO , A.ORIGIN_NAME , A.FILE_NAME , A.orderDate FROM (SELECT ROWNUM RNUM , B.CART_AMOUNT , B.CATEGORY_NO , B.ITEM_NO , B.CART_NO , B.ITEM_NAME , B.ITEM_STANDARD , B.ITEM_SALES , B.ITEM_MAKE , B.FILE_NO , B.ORIGIN_NAME , B.FILE_NAME , B.orderDate FROM (SELECT C.CART_AMOUNT , C.CATEGORY_NO , C.ITEM_NO , C.CART_NO , E.ITEM_NAME , E.ITEM_STANDARD , E.ITEM_SALES , E.ITEM_MAKE , G.FILE_NO , G.ORIGIN_NAME , G.FILE_NAME , TO_CHAR(F.ORDER_DATE, 'YY/DD/MM') AS orderDate FROM CART C LEFT JOIN ORDER_HANDLER D ON (C.CART_NO = D.CART_NO) LEFT JOIN ITEM_INFO E ON (C.ITEM_NO = E.ITEM_NO) LEFT JOIN STORE_ORDER F ON (D.ORDER_NO = F.ORDER_NO) LEFT JOIN ITEM_FILE G ON (C.ITEM_NO = G.ITEM_NO)
TO_CHAR(F.ORDER_DATE, 'YY/MM/DD') = #{ orderDate }
AND C.STORE_NAME = #{ storeName }
ORDER BY C.ITEM_NO ASC ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }
