mapper.select:
SELECT A.RNUM , A.R_NO , A.R_DATE , A.RETURN_PROGRESS , TO_CHAR(RP.R_DONE_DATE,'yyyy-MM-dd') AS R_DONE_DATE FROM (SELECT ROWNUM RNUM , B.R_NO , B.R_DATE , B.RETURN_PROGRESS FROM (SELECT DISTINCT C.R_NO , TO_CHAR(C.R_DATE,'yyyy-MM-dd') AS R_DATE , C.RETURN_PROGRESS FROM RETURN_ITEM C ORDER BY C.R_NO ASC ) B
WHERE ROWNUM <= #{ endRow }
) A JOIN RETURN_PROGRESS RP ON (A.R_NO = RP.R_NO) WHERE A.RNUM >= #{ startRow } ORDER BY A.R_NO ASC
